class Controller {
	constructor(schema, env){
		this._schema = schema // Job, Post, Profile, etc
	}

	static instance() {
		return new this()
	}

	instance() {
		return this
	}

	static parseFilters(params) {
		if (params == null)
			return null

		const filters = {
			sort: (params.sort == 'asc') ? 'timestamp' : '-timestamp',
			limit: (params.limit) ? parseInt(params.limit) : 0
		}

		delete params['sort']
		delete params['limit']
		return filters
	}

	collectionName() {
		return this._schema.collectionName()
	}

	schema() {
		return this._schema.schema()
	}

  	get(params) {
		return null
	}

	getById(id) {
		return null
	}

	post(body) {
		return null
	}

	put(id, params) {
		return null
	}

	delete(id) {
		return null
	}
}

module.exports = Controller
